package buri.ec.y24.q01

import buri.ec.common.BasePuzzle
import buri.ec.common.Part
import org.junit.Test

/**
 * Entry point for a daily puzzle
 *
 * @author Brian Uri!
 */
class Puzzle : BasePuzzle() {

    private val input1 = """
        
    """.trimIndent()

    private val input2 = """
        
    """.trimIndent()

    private val input3 = """
        
    """.trimIndent()

    @Test
    fun runPart1() {
        assertRun(5, "ABBAC")
        assertRun(1334, input1, true)
    }

    @Test
    fun runPart2() {
        assertRun(28, "AxBCDDCAxD")
        assertRun(5383, input2, true)
    }

    @Test
    fun runPart3() {
        assertRun(30, "xBxAAABCDxCC")
        assertRun(28000, input3, true)
    }

    /**
     * Executes a part of the puzzle using the specified input file.
     */
    override fun run(part: Part, input: String): Number {
        val battles = input.chunked(part.number)
        var sum = 0
        for (battle in battles) {
            for (mob in battle) {
                sum += when (mob) {
                    'B' -> 1
                    'C' -> 3
                    'D' -> 5
                    else -> 0   // A or x
                }
            }
            val mobCount = part.number - battle.count { it == 'x' }
            sum += when (mobCount) {
                2 -> 2
                3 -> 6
                else -> 0       // 0 or 1 mobs
            }
        }
        return sum
    }
}